// src/FaceScan.js
import React, { useState, useRef } from 'react';
import { Container, Paper, Typography, Box, Button } from '@mui/material';

function FaceScan() {
  // 状態管理
  // mode: 'initial' | 'capture' | 'captureDone' | 'fileDone' | 'result'
  const [mode, setMode] = useState('initial');
  const [imageData, setImageData] = useState('');
  const [resultText, setResultText] = useState('');

  // ref を使って DOM 要素にアクセス
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // -------------------------
  // 診断開始: カメラを起動して撮影モードへ
  const handleStartScan = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'user' },
      });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
      }
      setMode('capture');
    } catch (err) {
      alert('カメラのアクセスが許可されていません。設定を確認してください。');
      console.error(err);
    }
  };

  // -------------------------
  // 撮影処理: video の内容を canvas にキャプチャ
  const handleCapture = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (video && canvas) {
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const ctx = canvas.getContext('2d');
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      const dataURL = canvas.toDataURL('image/jpeg', 0.7);
      setImageData(dataURL);
      // カメラストリームを停止
      if (video.srcObject) {
        video.srcObject.getTracks().forEach((track) => track.stop());
      }
      setMode('captureDone');
    }
  };

  // -------------------------
  // ファイル選択処理: 画像参照の場合の処理
  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (event) => {
      setImageData(event.target.result);
      setMode('fileDone');
    };
    reader.readAsDataURL(file);
  };

  // -------------------------
  // 診断処理: バックエンド API に画像データを送信して診断結果を取得
  const handleAnalyze = async () => {
    if (!imageData) {
      alert('画像がありません！');
      return;
    }
    try {
      const response = await fetch('https://facescan-api.onrender.com/api/upload', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image: imageData }),
      });
      const json = await response.json();
      setResultText(json.result);
      setMode('result');
    } catch (err) {
      alert('診断に失敗しました');
      console.error(err);
    }
  };

  // -------------------------
  // 再撮影: 撮影モードに戻す
  const handleReCapture = async () => {
    setImageData('');
    setResultText('');
    handleStartScan();
  };

  // -------------------------
  // 画像再選択: ファイル入力を再度促す
  const handleReselect = () => {
    setImageData('');
    setMode('initial');
  };

  // -------------------------
  // ファイル選択から撮影モードへの切り替え
  const handleTakePhoto = () => {
    setImageData('');
    setMode('capture');
    handleStartScan();
  };

  // -------------------------
  // リセット処理: 初期状態に戻す
  const handleRetry = () => {
    setMode('initial');
    setImageData('');
    setResultText('');
  };

  // -------------------------
  // 診断結果表示用コンポーネント
  const renderResult = () => (
    <Paper sx={{ p: 2, backgroundColor: '#f9f9f9' }}>
      <Typography variant="h6">【診断結果】</Typography>
      {resultText.split('\n').map((line, idx) => (
        <Typography key={idx}>{line}</Typography>
      ))}
    </Paper>
  );

  return (
    <Container maxWidth="sm" sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h5" align="center" gutterBottom>
          FaceScan 診断アプリ
        </Typography>

        {mode === 'initial' && (
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 2 }}>
            <Button variant="contained" onClick={handleStartScan}>
              診断を開始
            </Button>
            <Button variant="outlined" component="label">
              画像を参照
              <input type="file" hidden onChange={handleFileSelect} />
            </Button>
          </Box>
        )}

        {mode === 'capture' && (
          <Box sx={{ mt: 2 }}>
            <video ref={videoRef} style={{ width: '100%' }} />
            <Box sx={{ mt: 2, display: 'flex', justifyContent: 'center', gap: 2 }}>
              <Button variant="contained" onClick={handleCapture}>
                撮影
              </Button>
            </Box>
          </Box>
        )}

        {(mode === 'captureDone' || mode === 'fileDone') && (
          <Box sx={{ mt: 2, textAlign: 'center' }}>
            {imageData && <img src={imageData} alt="Captured" style={{ maxWidth: '100%' }} />}
            <Box sx={{ mt: 2, display: 'flex', justifyContent: 'center', gap: 2 }}>
              <Button variant="contained" onClick={handleAnalyze}>
                この写真で診断
              </Button>
              {mode === 'captureDone' && (
                <Button variant="outlined" onClick={handleReCapture}>
                  再撮影する
                </Button>
              )}
              {mode === 'fileDone' && (
                <>
                  <Button variant="outlined" onClick={handleReselect}>
                    画像を選びなおす
                  </Button>
                  <Button variant="outlined" onClick={handleTakePhoto}>
                    写真を撮影する
                  </Button>
                </>
              )}
            </Box>
          </Box>
        )}

        {mode === 'result' && (
          <Box sx={{ mt: 2 }}>
            {resultText ? renderResult() : <Typography>診断結果を表示中...</Typography>}
            <Box sx={{ mt: 2, display: 'flex', justifyContent: 'center', gap: 2 }}>
              <Button variant="contained" onClick={handleRetry} color="secondary">
                もう一回診断する
              </Button>
              {/* シェアボタンはここに追加可能 */}
            </Box>
          </Box>
        )}
      </Paper>
      {/* 隠し canvas 要素 */}
      <canvas ref={canvasRef} style={{ display: 'none' }} />
    </Container>
  );
}

export default FaceScan;
